mapper.select:
select
  id, platform, message_type, mem_role, mem_no, order_no, content, page_mode, url, event_name, param, event, send_time, create_op, order_supplement_detail_id
from order_center.supplement_sendmsg_log


        and id = #{id}


        and platform = #{platform}


        and message_type = #{messageType}


        and mem_role = #{memRole}


        and mem_no = #{memNo}


        and order_no = #{orderNo}


        and content = #{content}


        and page_mode = #{pageMode}


        and url = #{url}


        and event_name = #{eventName}


        and param = #{param}


        and event = #{event}


        and send_time = #{sendTime}


        and create_op = #{createOp}


        and order_supplement_detail_id = #{orderSupplementDetailId}
