mapper.update:
        update renter_order_wz_settle_flag
        set has_illegal = #{hasIllegal},
            update_op = #{updateOp}
        where order_no = #{orderNo}
		and car_plate_num = #{carNum}

update renter_order_wz_settle_flag
set has_illegal_cost = #{hasIllegalCost},
	update_op = #{updateOp}
where order_no = #{orderNo}
and car_plate_num = #{carNum}

        update renter_order_wz_settle_flag
        set settle_flag = #{settleFlag},
			settle_time = NOW(),
			update_op = #{updateOp}
        where order_no = #{orderNo}
