mapper.update:
UPDATE renter_order_wz_status
SET management_mode = #{managementMode}
where order_no = #{orderNo} and car_plate_num = #{carNum} and is_delete = 0

mapper.select:
SELECT w.id,w.order_no as orderNo,f.rent_time as rentTime, IFNULL(p.id,0) as wzProcessedProof,
f.revert_time as revertTime, w.car_plate_num as plateNum,
f.frame_no as frameNo,f.engine_num as engineNo,
f.city_name as rentCity,w.status as wzStatus,
w.wz_handle_complete_time as wzStatusChangeTime,
w.create_time as enterWzManageTime, f.cities as onlineCity
FROM renter_order_wz_status w
LEFT JOIN renter_order_wz_finished_todo f on w.order_no = f.order_no and w.car_plate_num = f.car_plate_num
LEFT JOIN renter_order_wz_illegal_photo p on w.order_no = p.order_no and w.car_plate_num = p.car_plate_num


        and w.order_no = #{orderNo}


        and f.city_name = #{useCarCity}


        and w.car_plate_num = #{carNo}


        and w.wz_handle_complete_time >= #{mixViolationManagerDate}


        and w.wz_handle_complete_time <= #{maxViolationManagerDate}


        and w.illegal_query = #{violationInfo}


        and w.status = #{violationStatus}


        and  DATE_FORMAT(f.rent_time,'%Y-%m-%d') = #{getCarDate}


        and  DATE_FORMAT(f.revert_time,'%Y-%m-%d') = #{returnCarDate}


        and p.id is null


        and p.id is not null

    and w.is_delete = 0
